package JavaProgramms;

public class MultiplesofTwo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		multipelof2();
	}

	public static void multipelof2() {
		int multipelof2, i;
		for (i = 0; i <= 100; i += 2) {

			System.out.println("Multiples of 2 from 1 to 100 : " + i);
		}
	}

}
